input_curah_hujan = float(input('Masukkan nilai curah hujan : '))
print('Cuaca Terang/Berawan') if input_curah_hujan == 0 else(print('Curah hujan ringan') if input_curah_hujan <= 20 and input_curah_hujan > 0.4 else(print('Curah hujan sedang') if input_curah_hujan >= 21 and input_curah_hujan <= 50 else(print('Curah hujan lebat') if input_curah_hujan >= 51 and input_curah_hujan <= 100 else(print('Curah hujan ekstrem') if input_curah_hujan > 100 else print('Angka yang diinputkan salah')))))